var accounts = [];
//Account Object
//balance
//username


//createAccount(account)
//push onto account array
// return account
function createAccount(account){
    accounts.push(account);
    return account;
}


//getAccount(username)
//find matching account using forEach
function getAccount(username) {
    var matchedAccount;
    accounts.forEach(function (account) {
        //check if the username is equal to the username entered in the function
        if (account.username === username) {
            matchedAccount = account;
        }
    });
    return matchedAccount;

}

// deposit money
function deposit(account,amount) {
    account.balance += amount;
}

// withdraw money
function withdraw(account,amount) {
    account.balance -= amount;
}

//getBalance(account)
function getBalance(account) {
    return account.balance;
}

// my account
//create an objecty
var johnsAccount = createAccount({
    username : 'john',
    balance : 0
});

deposit(johnsAccount, 20000);
console.log(johnsAccount.balance);

withdraw(johnsAccount,1450);
console.log(johnsAccount.balance);

var exsistingAccount = getAccount('john');
console.log(exsistingAccount);